<?phpnamespace App\Helpers;use App\Models\AppointmentType;use App\Models\Booking;use App\Models\Country;use App\Models\DateTime;use App\Models\DoctorDetails;use App\Models\Service;use App\Models\User;use App\Models\UserDetails;use App\Providers\HelperServiceProvider;use Carbon\Carbon;use App\Models\Settings;use DB;use Hash;use Auth;use Mail;use DataTables;use Session;use Redirect;use Alert;use Input;class UserHelper extends HelperServiceProvider{    static function getHeaderFooter()    {        $settings=Settings::select('value','option')->pluck('value','option');        return $settings;    }    public static function service(){        $service=Service::select('*')->get();        return $service;    }    static function doctorservice()    {        $doctor=UserDetails::select('user_details.*')                ->leftjoin('users','users.id','=','user_details.user_id')                ->where('user_type','=','doctor')                ->get();      //dd($service);       //$doctor=DoctorDetails::where('service_id','=',$service->id)->get()->first();        //dd($doctor);        return $doctor;    }    static function userById($user_id)    {        $user=UserDetails::select('user_details.*')            ->leftjoin('users','users.id','=','user_details.user_id')            ->where('users.id','=',$user_id)            ->get()->first();        return $user;    }    static function appointmentType($id)    {        $appotype = AppointmentType::select('appointment_type.*')            ->leftjoin('booking','booking.service_type','=','appointment_type.id')            ->where('booking.service_type','=',$id)            ->get()->first();        //$appotype=AppointmentType::get()->first();        return $appotype;    }    static function datetime()    {        $datetime=DateTime::get();        return $datetime;    }    static function patientinfo()    {        $user_session=Session::get('user');        //dd($user_session);die;        $profile=User::find($user_session['user_id']);        $patient=UserDetails::where('user_id','=',$profile->id)->get()->first();        //dd($patient);die;        //$patient=UserDetails::where()->get()->first();        return $patient;    }    static function askquestion()    {        $user_session = Session::get('user');        //dd($user_session);die;        //$profile = User::find($user_session['user_id']);    }    static function doctor()    {        $user_session=Session::get('user');        //dd($user_session);die;        $profile=User::find($user_session['user_id']);        $doctor=DoctorDetails::where('user_id','=',$profile->id)->get()->first();        //dd($patient);die;        //$patient=UserDetails::where()->get()->first();        return $doctor;    }    static function country()    {        $country=Country::get();        return $country;    }}